import React from "react";
import Timeline from "./components/Timeline";
import Header from "./components/Header";

const App: React.FC = () => {
  const toggleTheme = () => {
    document.body.classList.toggle("dark-theme");
  };

  return (
    <>
      <Header onToggleTheme={toggleTheme} />
      <div className="container">
        <Timeline />
      </div>
    </>
  );
};

export default App;
