import React, { useState } from "react";
import { Event } from "../types";
import TimelineCard from "./TimelineCard";

interface TimelineProps {
  events: Event[];
  onCardClick: (event: Event) => void;
}

const Timeline: React.FC<TimelineProps> = ({ events, onCardClick }) => {
  const [activeIndex, setActiveIndex] = useState(0);

  return (
    <section id="timeline-container">
      {/* Dates + Dots together */}
      <div className="date-container">
        <div className="date-row">
          {events.map((e, i) => (
            <div key={i} className="date-dot-wrapper">
              {/* Date */}
              <span className="date-label">{e.year}</span>
              {/* Dot below date */}
              <div
                className={`dot ${i === activeIndex ? "active" : ""}`}
                onClick={() => setActiveIndex(i)}
              />
            </div>
          ))}
        </div>
        {/* Horizontal line behind dots */}
        <div className="timeline-line"></div>
      </div>

      {/* Active card below */}
      <div id="timeline">
        <TimelineCard
          event={events[activeIndex]}
          onClick={() => onCardClick(events[activeIndex])}
        />
      </div>
    </section>
  );
};

export default Timeline;
