// src/App.tsx
import React, { useEffect, useState } from "react";
import Header from "./components/Header";
import Timeline from "./components/Timeline";
import EventModal from "./components/EventModal";
import { Event } from "./types";

const App: React.FC = () => {
  const [events, setEvents] = useState<TimelineEvent[]>([]);
  const [selectedIdx, setSelectedIdx] = useState(0);
  const [modalOpen, setModalOpen] = useState(false);

  // Theme toggle (for demo)
  const onThemeToggle = () => {
    // (implement body class toggle if needed)
  };

  // Fetch events
  useEffect(() => {
    fetch("/events.json")
      .then((res) => res.json())
      .then(setEvents)
      .catch(console.error);
  }, []);

  return (
    <div>
      <Header onThemeToggle={onThemeToggle} />
      <Timeline
        events={events}
        selectedIdx={selectedIdx}
        setSelectedIdx={setSelectedIdx}
        onEventClick={() => setModalOpen(true)}
      />
      {modalOpen && events[selectedIdx] && (
        <EventModal event={events[selectedIdx]} onClose={() => setModalOpen(false)} />
      )}
    </div>
  );
};

export default App;
