import React, { useEffect, useState } from "react";
// import { EventData } from "./App";
// import EventMarker from "./EventMarker";
import TimelineCard from "./TimelineCard";
import { Event } from "../types";

interface TimelineProps {
  onCardClick: (event: Event) => void;
}

const Timeline: React.FC<TimelineProps> = ({ onCardClick }) => {
  const [events, setEvents] = useState<EventData[]>([]);
  const [activeIndex, setActiveIndex] = useState(0);

  // Load events.json from public/data
  useEffect(() => {
    fetch("/data/events.json")
      .then((res) => res.json())
      .then((data) => setEvents(data))
      .catch((err) => console.error("Error loading events:", err));
  }, []);

  if (events.length === 0) {
    return <p>Loading events...</p>;
  }

  return (
    <section>
      {/* Dots row with years */}
      <div id="dots-row">
        {events.map((event, index) => (
          <div key={event.year} className="dot-container">
            {/* Year above */}
            <div className="dot-year">{event.year}</div>

            {/* Dot itself */}
            <div
              className={`dots ${index === activeIndex ? "active" : ""}`}
              onClick={() => setActiveIndex(index)}
            />
          </div>
        ))}
      </div>

      {/* Timeline cards */}
      <div id="timeline">
        <Timeline
          event={events[activeIndex]}
          onClick={() => onCardClick(events[activeIndex])}
        />
      </div>
    </section>
  );
};

export default Timeline;
