import React, { useEffect, useState } from "react";
import { Event } from "../types";


const Timeline: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [selectedIdx, setSelectedIdx] = useState(0);

  useEffect(() => {
    fetch("/events.json")
      .then((res) => res.json())
      .then((data: Event[]) => setEvents(data))
      .catch(console.error);
  }, []);

  if (events.length === 0) return <div>Loading...</div>;

  return (
    <section id="timeline-container">
      {/* Dates row */}
      <div className="date-container">
        <div className="date">
          {events.map((event) => (
            <span key={event.year} style={{ color: "#1a56d9" }}>
              {event.year}
            </span>
          ))}
        </div>
      </div>

      {/* Timeline bar with dots below the dates */}
      <div className="timeline-bar">
        {events.map((event, idx) => (
          <div className="timeline-item" key={event.year}>
            <div
              className={`dots${selectedIdx === idx ? " active" : ""}`}
              onClick={() => setSelectedIdx(idx)}
              aria-label={`Select year ${event.year}`}
              role="button"
              tabIndex={0}
              onKeyDown={(e) => {
                if (e.key === "Enter" || e.key === " ") setSelectedIdx(idx);
              }}
            />
          </div>
        ))}
      </div>

      {/* Selected event card below */}
      <div className="timeline-card-row">
        <div className="timeline-event">
          <h3>
            {events[selectedIdx].year} - {events[selectedIdx].title}
          </h3>
          <img src={events[selectedIdx].image} alt={events[selectedIdx].title} />
          <p>{events[selectedIdx].description}</p>
        </div>
      </div>
    </section>
  );
};

export default Timeline;
