import React, { useState, useRef, useEffect } from "react";
import { Event } from "../types";
import EventMarker from "./EventMarker";

interface TimelineProps {
  events: Event[];
  onSelectEvent: (event: Event) => void;
}

const Timeline: React.FC<TimelineProps> = ({ events, onSelectEvent }) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const dotsRowRef = useRef<HTMLDivElement>(null);

  const handleDotClick = (index: number) => {
    setActiveIndex(index);
    onSelectEvent(events[index]);

    // Scroll clicked dot into center view
    const dot = dotsRowRef.current?.children[index] as HTMLElement;
    dot?.scrollIntoView({ behavior: "smooth", inline: "center" });
  };

  useEffect(() => {
    if (events.length > 0) onSelectEvent(events[0]);
  }, [events, onSelectEvent]);

  return (
    <section className="container">
      <div className="dots-container">
        <div className="dots-row" ref={dotsRowRef} style={{ display: "flex", gap: "40px", overflowX: "auto", whiteSpace: "nowrap", padding: "10px 0" }}>
          {events.map((event, index) => (
            <EventMarker
              key={event.year}
              event={event}
              isActive={index === activeIndex}
              onClick={() => handleDotClick(index)}
            />
          ))}
        </div>
      </div>

      <div id="timeline" style={{ display: "flex", gap: "20px", overflowX: "auto", padding: "10px 0" }}>
        {events.map((event) => (
          <div key={event.year} className="timeline-event" onClick={() => onSelectEvent(event)}>
            <h3>{event.title}</h3>
            <img src={event.image} alt={event.title} />
            <p>{event.description}</p>
            <span>{event.year}</span>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Timeline;
