// src/components/Timeline.tsx
import React from "react";
import { Event } from "../types";
import EventMarker from "./EventMarker";

type TimelineProps = {
  events: Event[];
  selectedIdx: number;
  setSelectedIdx: (idx: number) => void;
  onEventClick: () => void;
};

const Timeline: React.FC<TimelineProps> = ({
  events,
  selectedIdx,
  setSelectedIdx,
  onEventClick
}) => (
  <section id="timeline-container">
    <div className="date-container">
      <div className="date">
        {events.map(event => (
          <span key={event.year} style={{ color: "#1a56d9" }}>
            {event.year}
          </span>
        ))}
      </div>
    </div>
    <div className="dots-container">
      <div className="dots-row">
        {events.map((event, idx) => (
          <EventMarker
            key={event.year}
            active={selectedIdx === idx}
            onClick={() => setSelectedIdx(idx)}
            year={event.year}
          />
        ))}
      </div>
    </div>
    <div id="timeline">
      {events[selectedIdx] && (
        <div
          className="timeline-event"
          onClick={onEventClick}
          data-year={events[selectedIdx].year}
        >
          <h3>
            {events[selectedIdx].year} - {events[selectedIdx].title}
          </h3>
          <img src={events[selectedIdx].image} alt={events[selectedIdx].title} />
          <p>{events[selectedIdx].description.substring(0, 80)}...</p>
        </div>
      )}
    </div>
  </section>
);

export default Timeline;
